  },
  watch: {
    active() {
      console.log(this.artive)
      if (this.active) {
        this.startAnimation()
      } else {
        this.endAnimation()
      }
    }
  },
  mounted() {
    this.mainTextBlock = document.querySelector('.diagram-body_text')
    this.mainTextBlockHeight = this.mainTextBlock.clientHeight

    this.mainTextBlock.addEventListener('wheel', this.wheelText)

    this.mainTextBlock.addEventListener('touchstart', this.touchStartText)
    this.mainTextBlock.addEventListener('touchmove', this.touchMoveText)
    this.mainTextBlock.addEventListener('touchend', this.touchEndText)

    const svgNode = document.querySelector('.section-diagram svg')

    for (let i = 0; i < this.diagramObj.length; i++) {
      const path = svgNode.childNodes[i]

      if (path.setAttribute) {
        // console.log(path)
        // console.log(this.diagramObj[i].id)
        path.setAttribute('id', `diagram-path-${this.diagramObj[i].id}`)
        // console.log(path)

        path.getBoundingClientRect()
        path.addEventListener('click', this.clickDiagram)
      }

      const svg = document.getElementById(
        `diagram-svg-${this.diagramObj[i].id}`
      )
      const parser = new DOMParser()
      const htmlDoc = parser.parseFromString(
        `<g 
              xmlns="http://www.w3.org/2000/svg"
              xmlns:xlink="http://www.w3.org/1999/xlink"
              style="isolation:isolate">
                ${this.diagramObj[i].svgNode}
            </g>`,
        'text/xml'
      )

      svg.appendChild(htmlDoc.documentElement)
    }
    // this.endAnimation()
  },
  methods: {
    clickDiagram(e) {
      const id = e.target.getAttribute('id').slice(13)
      this.changeClass(id)
      this.changeActiveText()
    },

    changeClass(id) {
      for (let i = 0; i < this.diagramObj.length; i++) {
        if (this.diagramObj[i].id !== id) {
          window[`diagram-svg-${this.diagramObj[i].id}`].classList.remove(
            'active'
          )
          window[`diagram-text-${this.diagramObj[i].id}`].classList.remove(
            'active'
          )
        }
        window[`diagram-svg-${id}`].classList.add('active')
        window[`diagram-text-${id}`].classList.add('active')
      }
    },

    touchStartText(e) {
      if (
        (this.textBlockScrollSize < 0 && this.textBlockScrollPosition === 0) ||
        (this.textBlockScrollSize < 0 && e.deltaY > 0) ||
        (this.textBlockScrollPosition <= 0 && e.deltaY < 0)
      ) {
        return
      }

      this.touchTextObject = [
        e.changedTouches[0].clientX,
        e.changedTouches[0].clientY
      ]

      if (!(this.textBlockScrollDirection === undefined)) {
        console.log('Start Touch Text.')
        return
      }

      e.stopPropagation()
    },

    touchMoveText(e) {
      if (this.touchTextObject === undefined) {
        return
      }

      const shiftY = this.touchTextObject[1] - e.changedTouches[0].clientY

      if (
        (this.textBlockScrollDirection === 'bottom' && shiftY > 0) ||
        (this.textBlockScrollDirection === 'top' && shiftY < 0)
      ) {
        return
      }

      this.textBlock.style.transition = 'none'
      this.textBlock.style.transform = `translate3d(0px, ${-this
        .textBlockScrollPosition - shiftY}px, 0px)`

      e.stopPropagation()
    },

    touchEndText(e) {
      if (this.touchTextObject === undefined) {
        return
      }

      const shiftY = this.touchTextObject[1] - e.changedTouches[0].clientY

      if (
        (this.textBlockScrollDirection === 'top' && shiftY < 0) ||
        (this.textBlockScrollDirection === 'bottom' && shiftY > 0)
      ) {
        this.textBlockScrollDirection = undefined
        return
      }

      this.textBlockScrollPosition += shiftY
      if (this.textBlockScrollPosition < 0) {
        this.textBlockScrollPosition = 0
        this.textBlockScrollDirection = 'top'
        this.textBlock.style.transition = '.2s'
        this.textBlock.style.transform = `translate3d(0px, 0px, 0px)`
      } else if (this.textBlockScrollPosition > this.textBlockScrollSize) {
        this.textBlockScrollPosition = this.textBlockScrollSize
        this.textBlockScrollDirection = 'bottom'
        this.textBlock.style.transition = '.2s'
        this.textBlock.style.transform = `translate3d(0px, ${-this
          .textBlockScrollSize}px, 0px)`
      }

      e.stopPropagation()
    },

    wheelText(e) {
      let step

      if (
        (this.textBlockScrollSize < 0 && this.textBlockScrollPosition === 0) ||
        (this.textBlockScrollSize < 0 && e.deltaY > 0) ||
        (this.textBlockScrollPosition <= 0 && e.deltaY < 0)
      ) {
        return
      }
      if (e.deltaY < 0) {
        step = -20
      } else {
        step = 20
      }

      this.textBlockScrollSize -= step
      this.textBlockScrollPosition += step
      this.textBlock.style.transform = `translate3d(0px, -${this.textBlockScrollPosition}px, 0px)`
      e.stopPropagation()
    },

    changeActiveText() {
      this.textBlock = document.querySelector('.diagram-body_text .active')
      this.textBlockScrollSize =
        this.textBlock.clientHeight - this.mainTextBlockHeight
      if (this.textBlockScrollSize < 0) {
        this.textBlock.style.top = `${(this.mainTextBlockHeight -
          this.textBlock.clientHeight) /
          2}px`
      }
      this.textBlockScrollPosition = 0
      this.textBlockScrollDirection = undefined
    },

    endAnimation() {
      const svgNode = document.querySelector('.section-diagram svg')

      for (let i = 0; i < this.diagramObj.length; i++) {
        const path = svgNode.childNodes[i]
        const length = path.getTotalLength()

        path.style.transition = '.5s'
        path.style.strokeDasharray = length + ' ' + length
        path.style.strokeDashoffset = length - 1
      }

      if (document.querySelector('.diagram-body_text .active') !== null) {
        document
          .querySelector('.diagram-body_text .active')
          .classList.remove('active')
        document
          .querySelector('.diagram-body_img svg.active')
          .classList.remove('active')
      }
    },

    startAnimation() {
      const svgNode = document.querySelector('.section-diagram svg')

      for (let i = 0; i < this.diagramObj.length; i++) {
        const path = svgNode.childNodes[i]
        const transitionOne = 4 - i * 0.4
        // const length = path.getTotalLength()

        setTimeout(function() {
          path.style.transition = `stroke-dashoffset ${transitionOne}s ease .4s`
          path.style.strokeDashoffset = 0
        }, 50)
      }

      const that = this
      const id = this.diagramObj[this.diagramObj.length - 1].id
      setTimeout(function() {
        document.getElementById(`diagram-svg-${id}`).classList.add('active')
        window[`diagram-text-${id}`].classList.add('active')
        that.changeActiveText()
      }, 500)
    },

    parseId(title) {
      if (title === 'Верстка') {
        return 'layout'
      } else {
        return title
          .toLowerCase()
          .replace(' ', '_')
          .replace('&', '_')
      }
    }
  }
}
</script>

